// src/components/ProductManagement.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ProductManagement = () => {
  const [products, setProducts] = useState([]);
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [quantity, setQuantity] = useState('');

  useEffect(() => {
    // Fetch products
    axios.get('/api/products')
      .then((response) => setProducts(response.data))
      .catch((err) => console.log('Error fetching products:', err));
  }, []);

  const handleAddProduct = () => {
    axios.post('/api/products', { name, description, price, quantity })
      .then((response) => {
        console.log(response.data);
        setProducts([...products, { name, description, price, quantity }]); // Add new product to state
      })
      .catch((err) => console.log('Error adding product:', err));
  };

  return (
    <div>
      <h2>Product Management</h2>
      <form>
        <input
          type="text"
          placeholder="Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <textarea
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <input
          type="number"
          placeholder="Price"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
        />
        <input
          type="number"
          placeholder="Quantity"
          value={quantity}
          onChange={(e) => setQuantity(e.target.value)}
        />
        <button type="button" onClick={handleAddProduct}>Add Product</button>
      </form>

      <h3>Existing Products</h3>
      <ul>
        {products.map((product, index) => (
          <li key={index}>
            <p>{product.name} - {product.price} USD</p>
            <button>Edit</button>
            <button>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ProductManagement;
