// src/components/SignupAndLogin.js
import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

const SignupAndLogin = ({ isLogin }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const history = useHistory();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const url = isLogin ? '/api/login' : '/api/signup';
      const response = await axios.post(url, { username, password });

      if (isLogin) {
        // Save the user data, maybe in context or localStorage
        console.log('Login successful:', response.data);
        history.push('/dashboard'); // Redirect to dashboard after successful login
      } else {
        console.log('Signup successful');
        history.push('/login'); // Redirect to login page after signup
      }
    } catch (err) {
      setError('Error: ' + err.response.data);
    }
  };

  return (
    <div>
      <h2>{isLogin ? 'Login' : 'Signup'}</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit">{isLogin ? 'Login' : 'Signup'}</button>
      </form>
      {error && <p>{error}</p>}
    </div>
  );
};

export default SignupAndLogin;
